import React from 'react';
import './Temporizador.css';


class Temporizador extends React.Component {
  state = {
    horas: undefined,
    minutos: undefined,
    segundos: undefined,
}

handleChangesegundos  (e) { 
    this.setState({segundos: e.target.value});
  }
handleChangeminutos  (e) { 
    this.setState({minutos: e.target.value});
  }
  handleChangeHora  (e) { 
    this.setState({horas: e.target.value});
  }
  
componentDidMount() {
    this.myInterval = setInterval(() => {
        const { segundos, minutos, horas } = this.state

        if (segundos > 0) {
            this.setState(({ segundos }) => ({
                segundos: segundos - 1
            }))
        }
        if (segundos === 0) {
          if (minutos > 0){
         
                this.setState(({ minutos }) => ({
                    minutos: minutos - 1,
                    segundos: 59
                }))
            }
        }
        if (segundos === 0) {
          if (minutos === 0) {
            if(horas === 0) {
            clearInterval(this.myInterval)
          } else {
              this.setState(({ horas }) => ({
                  horas: horas - 1,
                  minutos: 59,
                  segundos: 59,
              }))
          }
      }
    }
    }, 1000)
}

componentWillUnmount() {
    clearInterval(this.myInterval)
}

render() {
    const {horas, minutos, segundos } = this.state
    return (
      <div className='Temporizador'>
        <div>
            { horas === 0 && minutos === 0 && segundos === 0
                ? <h1>Fim!</h1>
                : <h1>Tempo restante:     {horas < 10 ? `0${horas}` : horas}:{minutos < 10 ? `0${minutos}` : minutos}:{segundos < 10 ? `0${segundos}` : segundos}</h1>
            }
        </div>
        <div>
        <input type="text" horas={this.state.value} onChange={this.handleChangehoras}/>
        <div>{this.state.value}</div>
    </div>
        </div>
    )
}
}
export default Temporizador;
